import requests, webbrowser
import PySimpleGUI as sg
from time import sleep

class Rastreio:
    def rastrear(self, codigo):
        self.codigo = codigo 
        url = f"https://api.linketrack.com/track/json?user=teste&token=1abcd00b2731640e886fb41a8a9671ad1434c599dbaa0a0de9a5aa619f29a83f&codigo={codigo}"
        payload = {}
        headers= {}
        response = requests.request("GET", url, headers=headers, data = payload)
        return response.json()
    
sg.theme('SandyBeach')
img = b''
menu = [
    ['&Arquivo',
     ['&Salvar',
      '&Abrir',
      '&Sair']
     ],
    ['&Ajuda',
     ['&Manual',
      '&Autor',
      ['&GitHub',
       '&Linkedin']
      ]
     ]
    ]

layout = [
    [sg.Menu(menu)],
    [sg.Text('Digite o CÓDIGO DE RASTREAMENTO de sua encomenda (tipo: AA123456789BR)')],
    [sg.Input('', size=(14, 1), font=('Arial', 32), key='-cod-'),
     sg.Button('Rastrear', font=('Arial', 32))],
    [sg.Stretch(), sg.Image(img), sg.Stretch()]
    ]
window = sg.Window('Rastreador de Encomendas', layout)

while True:    
    msg = ''
    rastreio = Rastreio()
    event, values = window.read()

# ------ Fechar janela -------
    if event == sg.WIN_CLOSED or event == 'Sair': 
        break
    
# ---- Abrir o linkedin do autor----
    elif event in ('Linkedin'):
        webbrowser.open_new_tab('https://www.linkedin.com/in/elizeu-barbosa-abreu-69965b218/')

# ---- Abrir o GitHub do Autor ----
    elif event in ('GitHub'):
        webbrowser.open_new_tab('https://github.com/elizeubarbosaabreu')
        
# ----- Manual do software ---
    elif event in ('Manual'):
        sg.popup('Manual do Rastreador de Encomendas',
                 '''
Este Rastreador de Encomendas foi inteiramente escrito em Python por Elizeu Barbosa Abreu (Confira mais projetos do autor no menu Ajuda/Autor)

Este rastreador permite o rastreio de encomendas transportadas pelos Correios, seja ela nacional ou internacional...

Foi adicionado o recurso de salvar (Disponível no menu Arquivo/Salvar, onde você salva os seus códigos individualmente para posterior pesquisa)...

Por enquanto, muito obrigado por usar nosso Software!!!
''')

# ------ Menu Rastrear -----   
    elif event in ('Rastrear'):        
        if values['-cod-'] == '':            
            window['-cod-'].update('')
                           
        try:
            cod = values['-cod-'].strip()
            rastreamento = (rastreio.rastrear(cod.upper()))
            eventos = rastreamento['eventos']
                
            for i in range(len(eventos)):
                info = str(eventos[i]["subStatus"])
                info = info.replace("[", '')
                info = info.replace("]", '')
                info = info.replace("'", '')
                msg += (f'''
Data: {eventos[i]["data"]} às {eventos[i]["hora"]}
Status: {eventos[i]["status"]} {info}
''')
            sg.popup(f'Status de {cod.upper()}:', msg)
            window['-cod-'].update('') 
            
        except:
            sg.popup_timed('!', 'Erro de digitação.\nPreencha com código de rastreio válido (AA123456789BR)!')

# --- salvar cod no computador -----
    elif event in ('Salvar'):
        filename = sg.popup_get_file('Salve seu Código de Rastreamento',
                                     title='Salvar Código de Rastreamento',
                                     file_types=(("Texto","*.txt"),),
                                     save_as = True,)
        with open(filename, 'w') as file:
            file.write(values['-cod-'].upper())
            
# --- Abrir arquivo do pc ---
    elif event in ('Abrir'):
        filename = sg.popup_get_file('Encontre seu Código de Rastreamento',
                                         title='Abrir Código de Rastreamento',
                                         file_types=(("Texto","*.txt"),),
                                         save_as = False,)
        with open(filename, 'r') as file:
            conteudo = file.read().strip()
            window['-cod-'].update(conteudo.upper())

window.close()
